SELECT UPPER(db_lbaudran.fiche_personne.nom) AS 'NOM', db_lbaudran.fiche_personne.prenom, db_lbaudran.abonnement.prix FROM db_lbaudran.fiche_personne INNER JOIN membre ON db_lbaudran.fiche_personne.id_perso = db_lbaudran.membre.id_fiche_perso INNER JOIN abonnement ON db_lbaudran.membre.id_abo = db_lbaudran.abonnement.id_abo WHERE db_lbaudran.abonnement.prix > 42 ORDER BY db_lbaudran.fiche_personne.nom, db_lbaudran.fiche_personne.prenom;
